import React, { useState } from "react";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

const DUMMY_DATA = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2019, 7, 14),
  },
  {
    id: 'e2',
    title: 'New TV',
    amount: 799.49,
    date: new Date(2020, 2, 12)
  },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 8, 28),
  },
  {
    id: 'e4',
    title: 'New Desk',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
  {
    id: 'e5',
    title: 'iPhone',
    amount: 999,
    date: new Date(2021, 5, 12),
  },
  {
    id: 'e6',
    title: 'iPhone',
    amount: 999,
    date: new Date(2019, 9, 12),
  },
];

const App = () => {

  const [expenses, setExpenses] = useState(DUMMY_DATA);

  const addNewExpenseHandler = (myExpenseData) => {
    setExpenses(prevExpenses => {
      return [myExpenseData, ...prevExpenses];
    });
  };

  return (
    <div>
      <NewExpense onAddNewExpense={addNewExpenseHandler} />
      <Expenses expenseItem={expenses} />
      <div className="visit-me">
        <a href="https://www.alexiglesias.in/#work" target="__blank" className="visit-me-link">Go back</a>
      </div>
    </div>
  );
};

export default App;
